selector = {
    "type": "passage",
}

try {
    if(msg.payload.criteria.includeIn === true) {
        orIn =  [
                { "set": "in" },
                { "predicted": "in" },
                { "simple": "in" }
                ]
    } else {
        orIn = []
    }
} catch(err) {
    orIn = []
}

try {

    if(msg.payload.criteria.includeOut===true) {
        orOut =  [
                { "set": "out" },
                { "predicted": "out" },
                { "simple": "out" }
                ]

    } else {
        orOut = []
    }
} catch(err) {
    orOut = []
}

try {
    if(msg.payload.criteria.includeInvalid===true) {
        orInvalid = [
                { "set": "invalid" },
                { "predicted": "invalid" },
                { "simple": "invalid" }
                ]
    } else {
        orInvalid = []
    }
} catch(err) {
    orInvalid = []
}

if(orIn.length + orOut.length + orInvalid.length > 0) {
    selector["direction"] = {
        "$or": [...orIn, ...orOut, ...orInvalid]
    } 
}

try {
    if(msg.payload.criteria.from) {
        andFrom = [ {"$gte": msg.payload.criteria.from } ]
    } else {
        andFrom = []
    }
} catch {
    andFrom = []
}

try {
    if(msg.payload.criteria.to) {
        andTo = [ {"$lte": msg.payload.criteria.to} ]
    } else {
        andTo = []
    }
} catch(err) {
    andTo = []
}

if(andFrom || andTo) {
    selector.start = {
        "$and": [...andFrom, ...andTo ]
    }
}


try {
    if(msg.payload.criteria.includeHeuristic) {
        selector.direction.$not = {
