queries = [{
    "query": 'UPDATE public.passage SET doc = ' +
    'jsonb_set(doc, \'{"direction"}\', doc->\'direction\' || $prediction, true) ' +
    'WHERE id = $id RETURNING *',
    "output": true,
    "params": {
        "prediction": JSON.stringify({
            "predicted": msg.prediction.pred,
            "score": msg.prediction.proba
        }),
        "id": msg.id
    }
}]

msg.payload = queries
return msg;

c = msg.payload.criteria

if(c.offset > 0) msg.load_mode = true

dirSet = []
if (c.includeIn) dirSet.push("'in'")
if (c.includeOut) dirSet.push("'out'")
if (c.includeInvalid) dirSet.push("'invalid'")
if (!c.includeIn && 
!c.includeOut && 
!c.includeInvalid) dirSet.push("'in','out','invalid'")
dirStr = dirSet.join(",")

directionFilter = "(" +
" (NOT doc->'direction' ?| array['set','predicted'] AND doc->'direction'->>'simple' in (" +dirStr+")) "+
"OR doc->'direction'->>'set' in (" +dirStr+") "+
"OR (NOT doc->'direction' ? 'set' AND doc->'direction'->>'predicted' in (" +dirStr+")))"

// DATE FILTER

dateFilter = "((doc->>'start')::float8 >= "+ (c.from ? c.from : 0) +
" AND (doc->>'end')::float8 <= "+(c.to ? c.to : "'+infinity'") + ") " 


orCond = []
if (c.includeHeuristic) 
    orCond.push("NOT (doc->'direction' ?| array['set','predicted'])")
if (c.includeEdgeCases) 
    orCond.push("(NOT doc->'direction' ? 'set' AND doc->'direction' ? 'predicted' AND (doc->'direction'->>'score')::float8 < 0.8 )")
if (orCond.length === 0) 
    orCond.push(" 1=1 ")
orCond = orCond.join(" OR ")

msg.payload = [{
    "query": 'SELECT * FROM public.passage ' +
    'WHERE 1 = 1 '+
    'AND '+ directionFilter + " "+
    'AND '+ (dateFilter ? dateFilter : " 1=1 ") +
    'AND ('+ orCond + ') ' +
    'ORDER BY id DESC ' +
    'LIMIT $limit OFFSET $offset ' ,
    "output": true,
    "params": {
        "limit": c.limit,
        "offset": c.offset,
    }

}]

return msg;

